// Compartilhar nas redes sociais
const share = (network: SocialNetwork) => {
  if (network.name === 'Link') {
    navigator.clipboard.writeText(network.url);
  } else {
    window.open(network.url, '_blank', 'noopener,noreferrer');
  }
};

